import React, {useState} from 'react';
import {Form, Input, Dropdown} from 'semantic-ui-react'
import Garment from './Garments'

const sizes = [
{ key: 1, text: 'X-small', value: 1},
{ key: 2, text: 'Small', value: 2},
{ key: 3, text: 'Medium', value: 3},
{ key: 4, text: 'Large', value: 4},
{ key: 5, text: 'X-Large', value: 5}
]

export const AddToCart = () => {
	const [newCart, setNewCart] = useState('');


	return (
		<Form key={garment.gtin} 
			key={garment.colorName} 
			key={garment.sizeName} 
			key={garment.piecePrice} 
			key={garment.colorFrontImage}>

			<Form.Field>
				<Input 
					style={{width: 100}}
					placeholder= "cart" 
					value={newCart} 
					onChange={e => setNewCart(e.target.value)}
				/>
			</Form.Field>

			<Form.Field>
				<Dropdown 
					placeholder= "sizes" 
					options= {sizes}
					value={newCart} 
					onChange={e => setNewCart(e.target.value)}
				/>
			</Form.Field>

			<Form.Field>
				<Dropdown 
					placeholder= "colors" 
					options= {garment.colorName}
					value={newCart} 
					onChange={e => setNewCart(e.target.value)}
				/>
			</Form.Field>

			<Form.Field>			
			</Form.Field>

		</Form>	


		)








}
